        if ((!bvatrack_get_cookie("Answered") || bvatrack_get_cookie("Answered") == 0) && (!bvatrack_get_cookie("capping") || parseInt(bvatrack_get_cookie("capping")) < bvatrack_cap)) {
          if (!bvatrack_get_cookie("trackEnabled") || bvatrack_get_cookie("trackEnabled") == "" || bvatrack_get_cookie("trackEnabled") == "0") {
            var rand = Math.random();
            if (bvatrack_rotate > rand || bvatrack_get_cookie("capping")) {
              if (bvatrack_trackingactive) {
                bvatrack_set_cookie("trackEnabled", "1");
                bvatrack_set_cookie("PageEntry", document.location);
                bvatrack_set_cookie("Page_1", document.location);
              }
              layerInit();
            } else {
              bvatrack_set_cookie("trackEnabled", "0");
            }

          } else {
            if (bvatrack_trackingactive) {
              if (bvatrack_get_cookie("trackEnabled") == "1") {
                if (document.location.hostname != getDomain(document.referrer)) {
                  bvatrack_set_cookie("Date1stVisit", new Date());
                  bvatrack_set_cookie("PageEntry", document.location);
                  for (i = bvatrack_loop; i > 1; i--) {
                    bvatrack_set_cookie("Page_" + i, null);
                  }
                  bvatrack_set_cookie("Page_1", document.location);
                  layerInit();
                } else {
                  for (i = bvatrack_loop; i > 1; i--) {
                    bvatrack_set_cookie("Page_" + i, bvatrack_get_cookie("Page_" + (i - 1)));
                  }
                  bvatrack_set_cookie("Page_1", document.location);
                }

