            }

          }
        }
      }
      if (document.getElementById(bvatrack_divmedaillon)) {
        if (bvatrack_get_cookie("trackEnabled") == "1" && bvatrack_get_cookie("Answered") != "1") {
          document.getElementById(bvatrack_divmedaillon).style.display = "";
        } else {
          document.getElementById(bvatrack_divmedaillon).style.display = "none";
        }
      }
    }
  }

